const initialState = {
    name: '',
    pin: '0000',
    cards: []
}

const SET_NAME = 'UserState/SET_NAME'
const SET_PIN = 'UserState/SET_PIN'
const ADD_CARD = 'UserState/ADD_CARD'
const REMOVE_CARD = 'UserState/REMOVE_CARD'

export function setName(name) {
    return { type: SET_NAME, payload: name}
}

export function setPin(pin) {
    return { type: SET_PIN, payload: pin}
}

export function addCard(card) {
    return { type: ADD_CARD, payload: card}
}

export function removeCard(card) {
    return { type: REMOVE_CARD, payload: card}
}

export default function UserStateReducer(state = initialState, action) {
    switch (action) {
        case SET_NAME:
            state.name = action.payload
            break
        case SET_PIN:
            state.pin = action.payload
            break
        case ADD_CARD:
            state.cards.push(action.payload)
            break
        case REMOVE_CARD:
            let foundIndex = -1
            for(let i = 0; i < state.cards.length; i++) {
                if(state.cards[i].cardInfo == action.payload.cardInfo) {
                    foundIndex = i
                }
            }
            if(foundIndex === -1) {
                alert('No Card Found')
                console.error('Card not found')
            } else {
                state.cards.splice(foundIndex, 1)
            }
            break
        default:
            return state
    }
    return state
}
